import './App.css';
import axios from "axios"

import React, {useState, useEffect} from "react"
import { Button, TextField, Typography, Box, Container, Paper, List } from '@mui/material';
import LoginIcon from '@mui/icons-material/Login';
import EditableMessage from './EditableMessage';

function App() {
  const [info, setInfo] = useState();
  const [usersMessages, setUsersMessages] = useState();
  const [message, setMessage] = useState("")
  const [username, setUsername] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    getAllMessages()
  }, [])
  useEffect(() => {
    getMessagesForUser(username)
  }, [isLoggedIn])

  const getAllMessages = () => {
    fetch("http://localhost:9000/messages/info")
    .then((res) => res.json())
    .then((text) => setInfo(text.result))
    .catch((err) => console.log(err))
  }
  const getMessagesForUser = (user) => {
    fetch("http://localhost:9000/messages/info?user=" + user)
    .then((res) => res.json())
    .then((text) => setUsersMessages(text.result))
    .catch((err) => console.log(err))
  }

  const post = () => {
    axios.post("http://localhost:9000/messages/post/", {
      text: message,
      likes: 0,
      responses: [],
      username: username
    })
    .then((res) => console.log(res.data))
    .catch((err) => console.log(err))
    .then(getAllMessages())
    .then(getMessagesForUser())
  }

  const updateMessage = (e) => {
    setMessage(e.target.value)
  }
  const updateUsername = (e) => {
    setUsername(e.target.value)
  }

  console.log(info)
  const usersMessagesList = usersMessages ? usersMessages.map((mes) => {
    return (
      <EditableMessage key={mes.id} messageInfo={mes} onDelete={() => getMessages(username)}/>
    )
  }) : null
  const allMessagesList = info ? info.map((mes) => {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <Typography variant='body1'>{mes.text}</Typography>
      </Box>
    )
  }) : null

  return (
    <React.Fragment>
      <Typography variant='h3'>{isLoggedIn ? "Message Board" : "Message Board Login"}</Typography>
      <Box>
        <TextField onChange={updateUsername} placeholder='Username' />
        <Button onClick={() => setIsLoggedIn(true)} variant='outlined' endIcon={<LoginIcon />}>Login</Button>
      </Box>
      {isLoggedIn && 
      <React.Fragment>
        <Box>
          <TextField onChange={updateMessage} placeholder='Message' />
          <Button onClick={() => post()} variant='outlined'>Post</Button>
        </Box>
        <Box>
          <Container>
            <Typography variant='h6'>Your Messages</Typography>
            <Paper style={{ maxHeight: '100%', overflow: 'auto' }}>
              <List>
                {usersMessagesList}
              </List>
            </Paper>
          </Container>
          <Container>
            <Typography variant='h6'>All Messages</Typography>
            <Paper style={{ maxHeight: '100%', overflow: 'auto' }}>
              <List>
                {allMessagesList}
              </List>
            </Paper>
          </Container>
        </Box>
      </React.Fragment>
      }
    </React.Fragment>
  );
}

export default App;
