import { Box, Typography, Container, List, Paper, Divider } from "@mui/material"

function AllMessages({ info }) {
    const allMessagesList = info.map((mes) => {
        return (
          <Box>
            <Typography variant='h6' sx={{ p}}>{mes.username}</Typography>
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
              <Typography variant='body1'>{mes.text}</Typography>
                <Box sx={{ flexGrow: 1 }} />
                <Typography variant='body1'>{mes.likes}</Typography>
            </Box>
            <Divider/>
          </Box>
        )
      })

    return (
        <Container>
            <Typography variant='h6'>All Messages</Typography>
            <Paper style={{ maxHeight: '100%', overflow: 'auto' }}>
              <List>
                {allMessagesList}
              </List>
            </Paper>
        </Container>
    )
}

export default AllMessages
